mapper.select:
SELECT COUNT(SUBCOMMENT_ID)
FROM TBL_SUBCOMMENT_LIKE
WHERE SUBCOMMENT_ID= #{subcommentId}

mapper.delete:
DELETE FROM TBL_SUBCOMMENT_LIKE
WHERE USER_ID = #{userId}
AND SUBCOMMENT_ID = #{subcommentId} AND ID = #{id}

mapper.select:
SELECT COUNT(*)
FROM TBL_SUBCOMMENT_LIKE
WHERE SUBCOMMENT_ID = #{subcommentId}
AND USER_ID = #{userId}

mapper.delete:
DELETE FROM TBL_SUBCOMMENT_LIKE
WHERE SUBCOMMENT_ID = #{subcommentId}
AND USER_ID = #{userId}

mapper.select:
SELECT COUNT(*)
FROM TBL_SUBCOMMENT_LIKE
WHERE SUBCOMMENT_ID = #{subcommentId}
